    private void jBtn18ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtn18ActionPerformed
        history = jTxtDisplay.getText();
        display = display + "||" + history;
        jLabel1.setText(display);
        
        String expression = history;
        infix= String.valueOf(calculation.infixToPreFix(expression));
        history = "";

        endResult = String.valueOf(calculation.evaluatePrefix(infix));
        String ans = String.valueOf(endResult);
        jTxtDisplay.setText(ans);
    }//GEN-LAST:event_jBtn18ActionPerformed

